lg = "en";

if(/\/(fr|de)/.exec(window.location)){
	lg =/\/(fr|de)/.exec(window.location)[1];
}

$(window).load(function(){


	url = window.location;
	if(/action=edit/.test(url)){
		$("a").click(function(e){
			if ($(this).attr('href') != '#') {
				if(!confirm("You are editing a page. Clicking on the link will leave the page and discard the changes")){
					e.preventDefault();
				}
			}
		});
	}

	$('.button-click').on('click', function (e) {
		var href = $('a',this).attr('href');
		window.location = href;
	});



if(window.matchMedia("(max-width:767px)").matches) {
	$mobileMenuButton = $('#mobile-menu-button');
	$mobileMenuButton.on('click', function(e){
		console.log('click menu');
		if ($mobileMenuButton.hasClass('bt-open')) {
				$mobileMenuButton.toggleClass('bt-open',false);
				$mobileMenuButton.toggleClass('bt-close',true);
				$('.mobile-menu').show();
		} else {
		if ($mobileMenuButton.hasClass('bt-close')) {
			$mobileMenuButton.toggleClass('bt-open',true);
			$mobileMenuButton.toggleClass('bt-close',false);
			$('.mobile-menu').hide();
		}
	}
	});


	$mobileSearchButton = $('#mobile-search-button');
	$mobileSearchButton.on('click', function(e){
		if ($mobileSearchButton.hasClass('bt-open')) {
				$mobileSearchButton.toggleClass('bt-open',false);
				$mobileSearchButton.toggleClass('bt-close',true);
				$('.mobile-search').show();
		} else {
		if ($mobileSearchButton.hasClass('bt-close')) {
				$mobileSearchButton.toggleClass('bt-close',false);
				$mobileSearchButton.toggleClass('bt-open',true);
				$('.mobile-search').hide();
		}
	}

	});

	$('div.thumb').each(function (idx, thumb) {
		$thumb = $(thumb);
		$imgLink = $thumb.find('.thumbinner > a').clone();
		$imgLink.empty();
		$caption = $thumb.find('.thumbinner > .thumbcaption').clone();
//$thumb.append($caption);
$picture = $('<div class="mobile-picture-icon"><span class="glyphicon glyphicon-picture"></span></div>');
		$caption.find('div.magnify').remove();
		$imgLink.append($picture);
		captionText = $caption.html();
		var sourceIndexBegin = captionText.indexOf("- Source");
		if (sourceIndexBegin > 0 ) {
			captionText = captionText.substring(0,sourceIndexBegin);
		}
		$imgLink.append('<div class="mobile-picture-caption">'+captionText+'</div>');
		$thumb.removeClass('thumb').removeClass('tright').addClass('mobile-picture');
		$thumb.empty().append($imgLink);

	});
