		var chevron = $this.find('span.h2-chevron');
		if (chevron.hasClass('h2-chevron-open')) {
			chevron.removeClass('h2-chevron-open').removeClass('glyphicon-chevron-down').addClass('glyphicon-chevron-right').addClass('h2-chevron-close');
		} else {
				chevron.removeClass('h2-chevron-close').removeClass('glyphicon-chevron-right').addClass('glyphicon-chevron-down').addClass('h2-chevron-open');
		}


		var $next = $this.next();
		var tagName = $next.prop('tagName');
		while (tagName !== 'H2' && tagName!== undefined) {
				$next.toggle();
				var $next = $next.next();
				tagName = $next.prop('tagName');
		}
	}).append('<span class="h2-chevron h2-chevron-open glyphicon glyphicon-chevron-down"></span>').each(function(idx,h2) {
		if (window.location.hash === '') {
			$h2 = $(h2);
			$h2.find('span.h2-chevron').removeClass('h2-chevron-open').removeClass('glyphicon-chevron-down').addClass('glyphicon-chevron-right').addClass('h2-chevron-close');
			var $next = $h2.next();
			var tagName = $next.prop('tagName');
			while (tagName !== 'H2' && tagName!== undefined) {
					$next.hide();
					var $next = $next.next();
					tagName = $next.prop('tagName');
			}
		}
	});

}
