function checkPdfCacheStatus(pageid) {      // declare the countdown function.
	 //var $container = $(container);
	 (function step() {
		 $.getJSON( "http://127.0.0.1/statexp/extensions/StatexpPDFGenerator/StatexpPDFJobQueueManagerStatus.php?pageid="+pageid, function( data ) {
			 if (data.status !== 'done') {
				setTimeout(step, 30000);
			} else {
				location.reload();
			}
			});

	})();
}

